const App = () => {
    const course = {
        id: 1,
        name: 'Half Stack application development bloo face baby ü¶ç',
        parts: [
            {
                name: 'Fundamentals of React',
                exercises: 10,
                id: 1
            },
            {
                name: 'Using props to pass data',
                exercises: 7,
                id: 2
            },
            {
                name: 'State of a component',
                exercises: 14,
                id: 3
            },
            {
                name: 'Redux',
                exercises: 11,
                id: 4
            }
        ]
    }

    return <Course course={course} />
}

const Course = ({course}) => {
    return <div>
        <Header name = {course.name}/>
        <Content props = {course}/>
    </div>
}
const Header = ({name}) =>{
    return <h1>{name}</h1>;
}

const Part = ({name, exercises}) => {
    return(
        <p>{name} {exercises}</p>
    );
}
const Total = ({parts}) => {
    let sum = 0;
    parts.forEach((x)=>{
        sum += x.exercises;
    });
    return <p>total of {sum} exercises</p>
}

const Content = ({props}) => {
    return (
        <div>
            {props.parts.map((e, i) => {
                return <Part key={i} name={e.name} exercises={e.exercises} />
            })}
            <Total parts={props.parts}/>
        </div>
    );
}
export default App
