import {useState} from "react";

const Header = () => {
    return(
        <h1>Give Feedback</h1>
    );
}
const Buttons = (props) => {
    return(
    <div>
        <button onClick={props.handleGood}>good</button>
        <button onClick={props.handleNeutral}>neutral</button>
        <button onClick={props.handleBad}>bad</button>
    </div>
    );
}

const StatisticLine = ({text , value}) => {
    if(text === 'positive'){
        return <p>{text} {value}%</p>
    }
    return(
        <p>{text} {value}</p>
    );
}

const Stats = (props) => {
    const total = props.good + props.neutral + props.bad
    const average = (props.good - props.bad) / total
    const positive = (props.good / total) * 100
    return(
        <div>
            <h1>Statistics</h1>
            {total ?
                <table>
                    <tr><StatisticLine text='good' value={props.good} /></tr>
                    <tr><StatisticLine text='neutral' value={props.neutral} /></tr>
                    <tr><StatisticLine text='bad' value={props.bad} /></tr>
                    <tr><StatisticLine text='all' value={total} /></tr>
                    <tr><StatisticLine text='average' value={average} /></tr>
                    <tr><StatisticLine text='positive' value={positive} /></tr>
                </table>
                : <p>No feedback given</p>
            }
        </div>
    );
}

const Unicaf = () => {
    const [good, setGood] = useState(0)
    const [neutral, setNeutral] = useState(0)
    const [bad, setBad] = useState(0)

    return(
        <div>
            <Header />
            <Buttons handleGood = {() => setGood(good + 1)}
                     handleNeutral={() => setNeutral(neutral + 1)}
                     handleBad={() => setBad(bad + 1)}
            />
            <Stats good={good} neutral={neutral} bad={bad} />
        </div>
    );
}
export default Unicaf
